<?php/** *  */class YandexGeoService implements IGeoService {        const TranslateUrl = 'https://translate.yandex.net/api/v1.5/tr.json/translate';    const TranslateKey = 'trnsl.1.1.20140627T080013Z.5758668491fd5631.3d19252e4806c2c0dabcf296cd4076edb9bb9f67';    const GeocodeUrl = 'http://geocode-maps.yandex.ru/1.x/?format=json&lang=en_US&geocode=';    public function translateEnRu(array $words){        $url = self::TranslateUrl . '?lang=en-ru&key=' . self::TranslateKey;                foreach ($words as $word){            $url .= "&text=" . urlencode($word);        }                $ch = curl_init();        curl_setopt($ch, CURLOPT_URL, $url);        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);        $response = json_decode(curl_exec($ch));        curl_close($ch);                return $response->text;    }    public function geocode($address){        if(empty($address)){            return FALSE;        }        $url = self::GeocodeUrl . $address;        $output = Yii::app()->curl->get($url);        $response = json_decode($output);        return $this->_parseGeoResult($response);    }    public function reverseGeocode($coord) {        ;    }        private function _parseGeoResult($geoResult){        return $geoResult;    }}