<?phpclass ErrorController extends Controller {        //public $layout='//layouts/main';        public function actionError()    {//        var_dump($this->layout);        $error=Yii::app()->errorHandler->error;        if($error)        {            if(Yii::app()->request->isAjaxRequest) {                echo $error['message'];            }            else {                $this->render('error', $error);            }        }    }}